INSERT INTO public.questions (id, category, question, options, "correctAnswer", explanation, banca) VALUES
(451, 'Saúde do Trabalhador', 'De acordo com a NR-32, qual a conduta correta em relação à vacinação do trabalhador de saúde?', '[{"id": "A", "text": "A vacinação é opcional e de custo do trabalhador."}, {"id": "B", "text": "O empregador deve fornecer gratuitamente o programa de imunização contra Tétano, Difteria e Hepatite B."}, {"id": "C", "text": "A recusa do trabalhador em se vacinar não precisa ser documentada."}, {"id": "D", "text": "A vacina contra Influenza é a única obrigatória pela NR-32."}]', 'B', 'A NR-32 (32.2.4.17.1) estabelece que o empregador deve fornecer, gratuitamente, o programa de imunização ativa contra tétano, difteria, hepatite B e os estabelecidos no PCMSO. A recusa deve ser documentada.', 'IBFC'),
(452, 'Saúde do Trabalhador', 'Um técnico de enfermagem sofre um acidente com material perfurocortante (agulha) ao cuidar de um paciente com HIV. Qual é a primeira ação a ser tomada pelo profissional acidentado?', '[{"id": "A", "text": "Procurar o médico do paciente para iniciar quimioprofilaxia."}, {"id": "B", "text": "Lavar o local abundantemente com água e sabão, sem esfregar."}, {"id": "C", "text": "Preencher a CAT (Comunicação de Acidente de Trabalho) imediatamente."}, {"id": "D", "text": "Espremer o local para forçar o sangramento e eliminar o vírus."}]', 'B', 'A primeira conduta imediata é o cuidado local: lavar exaustivamente com água e sabão. Espremer o local é contraindicado. Após os cuidados locais, o profissional deve procurar o serviço de referência para avaliação e início da PEP (Profilaxia Pós-Exposição), se indicada, e a notificação do acidente (CAT) deve ser feita.', 'Vunesp'),
(453, 'Saúde do Trabalhador', 'A LER/DORT (Lesão por Esforço Repetitivo/Distúrbio Osteomuscular Relacionado ao Trabalho) é uma das principais doenças ocupacionais na enfermagem. Qual fator é considerado o principal risco ergonômico para seu desenvolvimento?', '[{"id": "A", "text": "Exposição a agentes biológicos."}, {"id": "B", "text": "Jornada de trabalho noturna."}, {"id": "C", "text": "Movimentação manual de pacientes e repetitividade de movimentos."}, {"id": "D", "text": "Estresse e pressão psicológica."}]', 'C', 'A movimentação de pacientes, posturas inadequadas e a alta repetitividade de movimentos (como puncionar acessos, preparar medicações) são os principais fatores de risco ergonômicos que levam ao desenvolvimento de LER/DORT na equipe de enfermagem.', 'FGV'),
(454, 'Saúde do Trabalhador', 'O que é a Comunicação de Acidente de Trabalho (CAT) e qual o prazo para sua emissão?', '[{"id": "A", "text": "É um relatório interno do hospital, sem prazo definido."}, {"id": "B", "text": "É um documento que deve ser emitido pelo próprio acidentado em até 72 horas."}, {"id": "C", "text": "É a notificação oficial do acidente à Previdência Social, que deve ser emitida pela empresa até o primeiro dia útil seguinte ao da ocorrência."}, {"id": "D", "text": "É um laudo médico que só pode ser emitido após 30 dias de afastamento."}]', 'C', 'A CAT é o documento que formaliza o acidente de trabalho perante o INSS. A empresa é obrigada a emiti-la até o primeiro dia útil após o acidente. Em caso de morte, a comunicação deve ser imediata.', 'Cesgranrio'),
(455, 'Saúde do Trabalhador', 'A NR-32 proíbe o uso de adornos nos postos de trabalho em serviços de saúde. Qual o principal objetivo dessa medida?', '[{"id": "A", "text": "Padronizar a aparência da equipe de enfermagem."}, {"id": "B", "text": "Evitar o risco de acidentes com equipamentos e o acúmulo de microrganismos."}, {"id": "C", "text": "Prevenir furtos e perdas de objetos de valor durante o plantão."}, {"id": "D", "text": "Reduzir a distração do profissional durante a assistência."}]', 'B', 'A proibição de adornos (anéis, pulseiras, relógios, colares) visa prevenir acidentes (como prender em equipamentos) e, principalmente, reduzir o risco de contaminação cruzada, já que esses objetos dificultam a higienização correta das mãos e podem abrigar patógenos.', 'AOCP'),
(456, 'Saúde Mental', 'A Lei 10.216/2001, conhecida como Lei da Reforma Psiquiátrica, redireciona o modelo assistencial em saúde mental. Qual é o princípio fundamental dessa lei?', '[{"id": "A", "text": "Priorizar a internação em hospitais psiquiátricos como principal forma de tratamento."}, {"id": "B", "text": "Garantir os direitos e a proteção das pessoas com transtornos mentais, visando a reinserção social."}, {"id": "C", "text": "Proibir qualquer tipo de internação psiquiátrica, mesmo em casos de risco."}, {"id": "D", "text": "Centralizar o tratamento psiquiátrico em grandes centros urbanos."}]', 'B', 'O eixo central da Reforma Psiquiátrica é a mudança do modelo hospitalocêntrico para um modelo de atenção comunitária, garantindo que a pessoa com transtorno mental seja tratada com dignidade, tenha seus direitos assegurados e seja reintegrada à sociedade.', 'Cebraspe'),
(457, 'Saúde Mental', 'Dentro da Rede de Atenção Psicossocial (RAPS), qual serviço é considerado o ordenador do cuidado e a principal porta de entrada para casos de saúde mental?', '[{"id": "A", "text": "Unidade Básica de Saúde (UBS)."}, {"id": "B", "text": "Hospital Geral com leitos de psiquiatria."}, {"id": "C", "text": "Centro de Atenção Psicossocial (CAPS)."}, {"id": "D", "text": "Ambulatório de Especialidades."}]', 'C', 'O CAPS é o dispositivo estratégico e ordenador da RAPS. Ele é responsável por acolher, tratar e articular o cuidado em rede para pessoas com transtornos mentais graves e persistentes, funcionando como porta de entrada especializada.', 'FCC'),
(458, 'Saúde Mental', 'Um paciente em surto psicótico, com risco iminente para si e para terceiros, recusa-se a receber tratamento. A família solicita a internação. Como se classifica esse tipo de internação segundo a Lei 10.216/2001?', '[{"id": "A", "text": "Internação Voluntária."}, {"id": "B", "text": "Internação Compulsória."}, {"id": "C", "text": "Internação Involuntária."}, {"id": "D", "text": "Internação Social."}]', 'C', 'A internação involuntária é aquela que ocorre sem o consentimento do paciente, mas com a solicitação de um terceiro (geralmente familiar). Ela deve ser comunicada ao Ministério Público em até 72 horas. A compulsória é determinada pela justiça.', 'Vunesp'),
(459, 'Saúde Mental', 'O que é o Projeto Terapêutico Singular (PTS) no contexto da saúde mental?', '[{"id": "A", "text": "Um protocolo de medicamentos padrão para cada tipo de transtorno."}, {"id": "B", "text": "Um plano de cuidados individualizado, construído pela equipe multiprofissional em conjunto com o usuário."}, {"id": "C", "text": "Um formulário de avaliação de risco de suicídio."}, {"id": "D", "text": "O nome dado à primeira consulta de acolhimento no CAPS."}]', 'B', 'O PTS é a principal ferramenta de gestão do cuidado na RAPS. É um plano de tratamento dinâmico e individualizado, que considera as necessidades, desejos e contexto de vida do usuário, sendo elaborado de forma corresponsável entre equipe, usuário e, se possível, família.', 'IBFC'),
(460, 'Saúde Mental', 'Os Serviços Residenciais Terapêuticos (SRT), também conhecidos como ''moradias assistidas'', são um componente da RAPS. Qual a sua finalidade principal?', '[{"id": "A", "text": "Servir como local de internação de curta permanência para crises."}, {"id": "B", "text": "Oferecer moradia para pessoas com longo histórico de internação psiquiátrica, visando a reinserção social."}, {"id": "C", "text": "Funcionar como hospital-dia para atividades terapêuticas."}, {"id": "D", "text": "Prestar atendimento de emergência psiquiátrica 24 horas."}]', 'B', 'Os SRTs são casas localizadas na comunidade, destinadas a acolher pessoas egressas de longas internações em hospitais psiquiátricos, que não possuem suporte familiar ou social. O objetivo é promover a autonomia e a reintegração na sociedade.', 'FGV'),
(461, 'Terapia Intensiva (UTI)', 'Qual é o principal objetivo da monitorização da Pressão Arterial Média (PAM) em um paciente em choque séptico?', '[{"id": "A", "text": "Avaliar a função cardíaca."}, {"id": "B", "text": "Garantir a perfusão tecidual adequada dos órgãos vitais."}, {"id": "C", "text": "Prevenir o desenvolvimento de hipertensão."}, {"id": "D", "text": "Monitorar o risco de sangramento."}]', 'B', 'A PAM é o principal indicador da pressão de perfusão dos órgãos. No choque, o objetivo é manter uma PAM mínima (geralmente ≥ 65 mmHg) para garantir que órgãos como cérebro, coração e rins recebam fluxo sanguíneo e oxigênio adequados.', 'EBSERH'),
(462, 'Terapia Intensiva (UTI)', 'Um paciente em ventilação mecânica invasiva (VMI) deve ter a pressão do cuff (balonete) do tubo orotraqueal mantida em qual faixa de valores para ser considerada segura?', '[{"id": "A", "text": "5 a 10 cmH2O."}, {"id": "B", "text": "20 a 30 cmH2O."}, {"id": "C", "text": "40 a 50 cmH2O."}, {"id": "D", "text": "Acima de 60 cmH2O."}]', 'B', 'A pressão do cuff deve ser mantida entre 20 e 30 cmH2O. Pressões abaixo de 20 aumentam o risco de broncoaspiração de secreções. Pressões acima de 30 aumentam o risco de isquemia e necrose da mucosa traqueal.', 'Cebraspe'),
(463, 'Terapia Intensiva (UTI)', 'A Escala de Agitação e Sedação de Richmond (RASS) é utilizada para titular a sedação em UTI. Um paciente que não responde ao chamado verbal, mas abre os olhos ou movimenta-se ao estímulo físico (toque), corresponde a qual nível na escala?', '[{"id": "A", "text": "RASS -2 (Sedação Leve)."}, {"id": "B", "text": "RASS -3 (Sedação Moderada)."}, {"id": "C", "text": "RASS -4 (Sedação Profunda)."}, {"id": "D", "text": "RASS -5 (Não Despertável)."}]', 'C', 'RASS -4 (Sedação Profunda) é definido como um paciente que não tem resposta ao estímulo verbal, mas apresenta qualquer movimento (ocular ou motor) ao estímulo físico. RASS -5 é a ausência de resposta a qualquer estímulo.', 'IBFC'),
(464, 'Terapia Intensiva (UTI)', 'Qual é o cuidado de enfermagem prioritário ao administrar uma droga vasoativa como a Noradrenalina?', '[{"id": "A", "text": "Administrar em acesso venoso periférico de pequeno calibre."}, {"id": "B", "text": "Fazer a infusão o mais rápido possível para corrigir a hipotensão."}, {"id": "C", "text": "Administrar preferencialmente em acesso venoso central e em bomba de infusão contínua."}, {"id": "D", "text": "Diluir a medicação em grande volume de Soro Fisiológico."}]', 'C', 'Drogas vasoativas são potentes e podem causar necrose tecidual grave se extravasarem. Por isso, a via de escolha é um acesso venoso central. A administração deve ser controlada rigorosamente por uma bomba de infusão contínua (BIC) para titular a dose e evitar flutuações perigosas da pressão.', 'Vunesp'),
(465, 'Terapia Intensiva (UTI)', 'O que compõe o ''bundle'' de prevenção de Pneumonia Associada à Ventilação (PAV)?', '[{"id": "A", "text": "Troca diária do circuito do ventilador e antibioticoprofilaxia."}, {"id": "B", "text": "Posição de Trendelenburg e aspiração traqueal a cada hora."}, {"id": "C", "text": "Cabeceira elevada (30-45º), higiene oral com clorexidina e despertar diário/teste de respiração espontânea."}, {"id": "D", "text": "Uso contínuo de bloqueador neuromuscular e sedação profunda."}]', 'C', 'O bundle de prevenção da PAV é um conjunto de medidas baseadas em evidências que, quando aplicadas juntas, reduzem significativamente a incidência da infecção. Os pilares são: manter a cabeceira elevada, realizar higiene oral com antisséptico (clorexidina), e avaliar diariamente a possibilidade de reduzir a sedação e testar a respiração espontânea.', 'FGV'),
(466, 'Administração em Enfermagem', 'Segundo a Resolução COFEN 543/2017, qual é a quantidade de horas de assistência de enfermagem por paciente, em 24 horas, para um leito de Cuidado Intensivo?', '[{"id": "A", "text": "6 horas."}, {"id": "B", "text": "10 horas."}, {"id": "C", "text": "12 horas."}, {"id": "D", "text": "18 horas."}]', 'D', 'A resolução estabelece os seguintes parâmetros de horas por paciente/24h: Cuidado Mínimo (4h), Intermediário (6h), Alta Dependência (10h), Semi-intensivo (10h) e Intensivo (18h).', 'IBFC'),
(467, 'Administração em Enfermagem', 'Um enfermeiro líder que toma decisões sozinho, centraliza o poder e foca estritamente no cumprimento de tarefas está exercendo qual estilo de liderança?', '[{"id": "A", "text": "Liderança Democrática."}, {"id": "B", "text": "Liderança Liberal (Laissez-faire)."}, {"id": "C", "text": "Liderança Autocrática."}, {"id": "D", "text": "Liderança Situacional."}]', 'C', 'A liderança autocrática é caracterizada pela centralização do poder e da tomada de decisão no líder, com pouca ou nenhuma participação da equipe. É um estilo útil em situações de emergência, mas pode desmotivar a equipe a longo prazo.', 'FCC'),
(468, 'Administração em Enfermagem', 'No contexto da auditoria em saúde, o que é uma ''glosa''?', '[{"id": "A", "text": "A aprovação total da conta hospitalar."}, {"id": "B", "text": "O cancelamento parcial ou total do pagamento de um procedimento ou material pela operadora de saúde."}, {"id": "C", "text": "O relatório de alta do paciente."}, {"id": "D", "text": "A autorização prévia para um procedimento de alto custo."}]', 'B', 'Glosa é a recusa do pagamento, por parte da fonte pagadora (convênio), de itens da conta hospitalar (procedimentos, materiais, medicamentos, diárias) por motivos técnicos, administrativos ou de cobrança indevida.', 'Vunesp'),
(469, 'Administração em Enfermagem', 'Qual a principal diferença entre Educação Permanente e Educação Continuada em saúde?', '[{"id": "A", "text": "Educação Continuada é para enfermeiros e Permanente é para técnicos."}, {"id": "B", "text": "Educação Permanente ocorre a partir da problematização do processo de trabalho, enquanto a Continuada são cursos e treinamentos pontuais."}, {"id": "C", "text": "Educação Permanente é obrigatória e a Continuada é opcional."}, {"id": "D", "text": "Não há diferença, os termos são sinônimos."}]', 'B', 'A Educação Continuada foca na atualização técnica e acúmulo de conhecimento (cursos, palestras). A Educação Permanente é mais profunda, partindo dos problemas do dia a dia do trabalho para gerar aprendizado e transformar a prática (aprender no trabalho, sobre o trabalho e para o trabalho).', 'Cebraspe'),
(470, 'Administração em Enfermagem', 'O planejamento estratégico em enfermagem utiliza a análise SWOT como ferramenta. O que a letra ''O'' representa nesta sigla?', '[{"id": "A", "text": "Objetivos (Objectives)."}, {"id": "B", "text": "Oportunidades (Opportunities)."}, {"id": "C", "text": "Organização (Organization)."}, {"id": "D", "text": "Otimização (Optimization)."}]', 'B', 'A análise SWOT (ou FOFA em português) avalia: Strengths (Forças - ambiente interno), Weaknesses (Fraquezas - ambiente interno), Opportunities (Oportunidades - ambiente externo) e Threats (Ameaças - ambiente externo).', 'FGV');