-- Lote adicional de 20 questões de alto nível para a categoria "Fundamentos de Enfermagem"

INSERT INTO public.questions (id, category, banca, question, options, "correctAnswer", explanation)
VALUES
    (9007, 'Fundamentos de Enfermagem', 'FCC', 'A respiração caracterizada por períodos de apneia que se alternam com ciclos de respirações de amplitude crescente e depois decrescente é denominada:', '[
        {"id": "A", "text": "Respiração de Kussmaul."},
        {"id": "B", "text": "Respiração de Cheyne-Stokes."},
        {"id": "C", "text": "Respiração de Biot."},
        {"id": "D", "text": "Respiração paradoxal."},
        {"id": "E", "text": "Ortopneia."}
    ]', 'B', 'A respiração de Cheyne-Stokes é um padrão cíclico com períodos de apneia seguidos por respirações que aumentam e depois diminuem em profundidade e frequência. É comum em pacientes com insuficiência cardíaca congestiva e lesões neurológicas.'),
    (9008, 'Fundamentos de Enfermagem', 'Vunesp', 'De acordo com a Lei do Exercício Profissional (Lei 7.498/86), ao identificar uma prescrição médica que apresenta risco ao paciente, a conduta correta do profissional de enfermagem é:', '[
        {"id": "A", "text": "Recusar-se a cumprir a prescrição e comunicar o fato ao médico prescritor e à chefia de enfermagem."},
        {"id": "B", "text": "Administrar a medicação e registrar no prontuário que foi por ordem médica."},
        {"id": "C", "text": "Alterar a prescrição por conta própria para uma dose segura."},
        {"id": "D", "text": "Solicitar que outro colega administre a medicação."},
        {"id": "E", "text": "Ignorar a prescrição e não administrar nada."}
    ]', 'A', 'O Código de Ética e a Lei do Exercício Profissional amparam o profissional a se recusar a cumprir prescrições que coloquem o paciente em risco. A conduta correta é questionar o prescritor, registrar o fato e comunicar à sua liderança imediata.'),
    (9009, 'Fundamentos de Enfermagem', 'IBFC', 'Na classificação de artigos hospitalares de Spaulding, um endoscópio flexível, que entra em contato com mucosas íntegras, é considerado um artigo:', '[
        {"id": "A", "text": "Crítico, exigindo esterilização."},
        {"id": "B", "text": "Semicrítico, exigindo desinfecção de alto nível."},
        {"id": "C", "text": "Não crítico, exigindo apenas limpeza."},
        {"id": "D", "text": "Descartável, não podendo ser reprocessado."},
        {"id": "E", "text": "Semicrítico, exigindo desinfecção de nível intermediário."}
    ]', 'B', 'Artigos que entram em contato com mucosas íntegras ou pele não íntegra são classificados como semicríticos. O processamento mínimo recomendado para esses itens é a desinfecção de alto nível, que elimina todos os microrganismos, exceto um alto número de esporos bacterianos.'),
    (9010, 'Fundamentos de Enfermagem', 'FGV', 'Ao preparar Insulina NPH e Insulina Regular na mesma seringa, a técnica correta de aspiração é:', '[
        {"id": "A", "text": "Aspirar primeiro a NPH (turva) e depois a Regular (límpida)."},
        {"id": "B", "text": "Aspirar primeiro a Regular (límpida) e depois a NPH (turva)."},
        {"id": "C", "text": "A ordem de aspiração não interfere no resultado."},
        {"id": "D", "text": "Injetar ar na NPH, injetar ar na Regular, aspirar a NPH e depois a Regular."},
        {"id": "E", "text": "Misturar os dois frascos antes de aspirar a dose total."}
    ]', 'B', 'A regra é "do límpido para o turvo". Primeiro aspira-se a Insulina Regular (ação rápida e límpida) e depois a NPH (ação intermediária e turva). Isso evita a contaminação do frasco de insulina rápida com a insulina de ação mais lenta.'),
    (9011, 'Fundamentos de Enfermagem', 'Cebraspe', 'A Meta 3 das Metas Internacionais de Segurança do Paciente visa melhorar a segurança na prescrição, no uso e na administração de medicamentos. Uma estratégia fundamental para esta meta é:', '[
        {"id": "A", "text": "Realizar a higienização das mãos antes e após o contato com o paciente."},
        {"id": "B", "text": "Identificar e rotular medicamentos de alta vigilância (High-Alert Medications)."},
        {"id": "C", "text": "Realizar o checklist de cirurgia segura em três tempos."},
        {"id": "D", "text": "Utilizar a escala de Morse para avaliar o risco de queda."},
        {"id": "E", "text": "Usar pelo menos dois identificadores para confirmar a identidade do paciente."}
    ]', 'B', 'A Meta 3 foca especificamente na segurança de medicamentos. A identificação de medicamentos de alta vigilância (eletrólitos concentrados, opioides, insulinas, anticoagulantes) com etiquetas de alerta e a implementação de protocolos de dupla checagem são estratégias centrais para prevenir erros graves.'),
    (9012, 'Fundamentos de Enfermagem', 'FCC', 'Qual é o método considerado padrão-ouro para a confirmação do posicionamento de uma Sonda Nasoenteral (SNE) para fins de alimentação?', '[
        {"id": "A", "text": "Ausculta epigástrica com injeção de 20ml de ar."},
        {"id": "B", "text": "Aspiração de conteúdo gástrico e teste de pH."},
        {"id": "C", "text": "Exame radiológico (Raio-X de abdome)."},
        {"id": "D", "text": "Imersão da ponta da sonda em copo com água."},
        {"id": "E", "text": "Medida da porção externa da sonda."}
    ]', 'C', 'O método padrão-ouro para confirmar o posicionamento de uma sonda enteral, especialmente as pós-pilóricas, é o exame radiológico. A ausculta é falha e perigosa. O teste de pH é um bom indicador à beira-leito, mas o Raio-X é o confirmatório definitivo.'),
    (9013, 'Fundamentos de Enfermagem', 'Vunesp', 'A limpeza de uma ferida cirúrgica limpa, com pontos, deve ser realizada com movimentos:', '[
        {"id": "A", "text": "Circulares, da borda para o centro da ferida."},
        {"id": "B", "text": "De cima para baixo, em toda a extensão da ferida."},
        {"id": "C", "text": "Do centro para as bordas (do mais limpo para o mais contaminado)."},
        {"id": "D", "text": "Das bordas para fora, afastando-se da incisão."},
        {"id": "E", "text": "Em zigue-zague, cobrindo toda a área."}
    ]', 'C', 'O princípio da limpeza de feridas é sempre do local considerado mais limpo para o mais contaminado. Em uma ferida cirúrgica fechada, o centro (incisão) é considerado o mais limpo, e as bordas e a pele perilesional são consideradas mais contaminadas.'),
    (9014, 'Fundamentos de Enfermagem', 'IBFC', 'A administração de medicamentos por via intradérmica (ID) é comumente utilizada para qual finalidade?', '[
        {"id": "A", "text": "Aplicação de vacinas de grande volume."},
        {"id": "B", "text": "Administração de antibióticos de ação rápida."},
        {"id": "C", "text": "Testes de sensibilidade (PPD) e provas alérgicas."},
        {"id": "D", "text": "Infusão de soro para hidratação."},
        {"id": "E", "text": "Aplicação de heparina e insulina."}
    ]', 'C', 'A via intradérmica tem absorção muito lenta, o que a torna ideal para testes de sensibilidade, como o PPD (teste tuberculínico) e testes alérgicos, onde a reação local é o objetivo da observação. A vacina BCG também utiliza essa via.'),
    (9015, 'Fundamentos de Enfermagem', 'FGV', 'O que é o hiato auscultatório, que pode ocorrer durante a aferição da pressão arterial?', '[
        {"id": "A", "text": "A diferença entre a pressão sistólica e diastólica."},
        {"id": "B", "text": "O som ouvido no final da aferição, indicando a pressão diastólica."},
        {"id": "C", "text": "Um período de silêncio entre os sons de Korotkoff, que pode levar a uma subestimação da sistólica."},
        {"id": "D", "text": "A ausência total de sons, indicando hipotensão severa."},
        {"id": "E", "text": "O primeiro som audível, que indica a pressão sistólica."}
    ]', 'C', 'O hiato auscultatório é um desaparecimento temporário dos sons de Korotkoff durante a desinsuflação do manguito. Se a pressão sistólica não for estimada previamente pelo método palpatório, o profissional pode inflar o manguito insuficientemente e registrar uma pressão sistólica falsamente baixa.'),
    (9016, 'Fundamentos de Enfermagem', 'Cebraspe', 'A NR-32, que trata da segurança e saúde no trabalho em serviços de saúde, proíbe expressamente o uso de adornos pelos trabalhadores. Qual o principal objetivo dessa proibição?', '[
        {"id": "A", "text": "Evitar a distração do profissional durante os procedimentos."},
        {"id": "B", "text": "Prevenir o risco de acidentes e a contaminação cruzada por microrganismos."},
        {"id": "C", "text": "Padronizar a aparência da equipe de enfermagem."},
        {"id": "D", "text": "Evitar furtos e perdas de objetos de valor no ambiente hospitalar."},
        {"id": "E", "text": "Impedir manifestações religiosas no local de trabalho."}
    ]', 'B', 'Adornos como anéis, pulseiras e relógios podem prender em equipamentos, causando acidentes. Além disso, acumulam microrganismos que não são eliminados com a higienização das mãos, tornando-se um veículo para a contaminação cruzada entre pacientes e profissionais.'),
    (9017, 'Fundamentos de Enfermagem', 'FCC', 'Na Sistematização da Assistência de Enfermagem (SAE), os termos NOC e NIC referem-se, respectivamente, a:', '[
        {"id": "A", "text": "Diagnósticos de Enfermagem e Problemas Colaborativos."},
        {"id": "B", "text": "Resultados Esperados e Intervenções de Enfermagem."},
        {"id": "C", "text": "Intervenções de Enfermagem e Resultados Esperados."},
        {"id": "D", "text": "Coleta de Dados e Avaliação."},
        {"id": "E", "text": "Planejamento e Implementação."}
    ]', 'B', 'NOC (Nursing Outcomes Classification) é a classificação dos Resultados de Enfermagem, ou seja, as metas que se espera alcançar com o paciente. NIC (Nursing Interventions Classification) é a classificação das Intervenções de Enfermagem, ou seja, as ações que serão realizadas para atingir os resultados.'),
    (9018, 'Fundamentos de Enfermagem', 'Vunesp', 'Qual o objetivo do Teste de Bowie-Dick realizado em autoclaves a vapor com pré-vácuo?', '[
        {"id": "A", "text": "Verificar a esterilidade da carga ao final do ciclo."},
        {"id": "B", "text": "Avaliar a eficácia da remoção de ar da câmara e a penetração do vapor."},
        {"id": "C", "text": "Monitorar a temperatura e a pressão durante o ciclo."},
        {"id": "D", "text": "Confirmar a morte de microrganismos esporulados."},
        {"id": "E", "text": "Testar a integridade da embalagem dos materiais."}
    ]', 'B', 'O Teste de Bowie-Dick é um indicador químico de Classe 2, realizado diariamente no primeiro ciclo com a autoclave vazia. Ele avalia a eficácia da bomba de vácuo em remover o ar da câmara, que é essencial para que o vapor sature e penetre em todos os pontos da carga.'),
    (9019, 'Fundamentos de Enfermagem', 'IBFC', 'A febre que se caracteriza por elevações térmicas que retornam à normalidade pelo menos uma vez em 24 horas é classificada como:', '[
        {"id": "A", "text": "Febre contínua."},
        {"id": "B", "text": "Febre remitente."},
        {"id": "C", "text": "Febre intermitente."},
        {"id": "D", "text": "Febre recorrente."},
        {"id": "E", "text": "Febrícula."}
    ]', 'C', 'A febre intermitente é caracterizada por picos febris que se alternam com períodos de temperatura normal (afebril) ao longo do dia. Na febre remitente, a temperatura oscila, mas nunca retorna ao normal.'),
    (9020, 'Fundamentos de Enfermagem', 'FGV', 'O fluxo de trabalho na Central de Material e Esterilização (CME) deve ser unidirecional. Qual a sequência correta das áreas?', '[
        {"id": "A", "text": "Preparo e Esterilização -> Expurgo -> Arsenal."},
        {"id": "B", "text": "Expurgo -> Preparo e Esterilização -> Arsenal."},
        {"id": "C", "text": "Arsenal -> Preparo e Esterilização -> Expurgo."},
        {"id": "D", "text": "Preparo e Esterilização -> Arsenal -> Expurgo."},
        {"id": "E", "text": "Expurgo -> Arsenal -> Preparo e Esterilização."}
    ]', 'B', 'O fluxo unidirecional na CME segue a lógica do "sujo para o limpo". Inicia-se no Expurgo (área suja, recebimento e limpeza), passa para a área de Preparo e Esterilização (área limpa, inspeção, empacotamento e esterilização) e termina no Arsenal (área estéril, guarda e distribuição).'),
    (9021, 'Fundamentos de Enfermagem', 'Cebraspe', 'A administração de medicamentos por via sublingual tem como principal vantagem:', '[
        {"id": "A", "text": "A possibilidade de administrar grandes volumes."},
        {"id": "B", "text": "A absorção lenta e gradual, ideal para medicamentos de uso contínuo."},
        {"id": "C", "text": "A ausência de sabor, facilitando a aceitação pelo paciente."},
        {"id": "D", "text": "A rápida absorção diretamente para a corrente sistêmica, evitando o metabolismo de primeira passagem."},
        {"id": "E", "text": "A necessidade de jejum para garantir a eficácia."}
    ]', 'D', 'A via sublingual possui uma rica vascularização que permite a absorção rápida do fármaco diretamente para a veia cava superior. Isso evita o metabolismo de primeira passagem hepática, onde parte da droga seria inativada pelo fígado, garantindo maior biodisponibilidade e início de ação mais rápido.'),
    (9022, 'Fundamentos de Enfermagem', 'FCC', 'O que é um problema colaborativo no contexto da Sistematização da Assistência de Enfermagem (SAE)?', '[
        {"id": "A", "text": "Um sinônimo para Diagnóstico de Enfermagem."},
        {"id": "B", "text": "Uma complicação fisiológica que a enfermagem monitora para detectar, mas não pode tratar de forma independente."},
        {"id": "C", "text": "Um problema que requer a colaboração do paciente para ser resolvido."},
        {"id": "D", "text": "Um diagnóstico médico que a enfermagem deve confirmar."},
        {"id": "E", "text": "Uma meta de enfermagem estabelecida em conjunto com a equipe multiprofissional."}
    ]', 'B', 'Problemas colaborativos são complicações reais ou potenciais que resultam de condições médicas ou tratamentos, e que a enfermagem não pode tratar de forma autônoma. A responsabilidade da enfermagem é monitorar, detectar precocemente e implementar as intervenções médicas e de enfermagem. Ex: "Hemorragia secundária a uso de anticoagulante".'),
    (9023, 'Fundamentos de Enfermagem', 'Vunesp', 'Qual o objetivo principal da Manobra de Valsalva durante a retirada de um cateter venoso central?', '[
        {"id": "A", "text": "Diminuir a dor do paciente durante o procedimento."},
        {"id": "B", "text": "Aumentar a pressão intratorácica para prevenir a embolia gasosa."},
        {"id": "C", "text": "Facilitar a visualização do óstio do cateter."},
        {"id": "D", "text": "Verificar a perviedade do cateter antes da remoção."},
        {"id": "E", "text": "Estimular o reflexo vagal para diminuir a frequência cardíaca."}
    ]', 'B', 'A Manobra de Valsalva (prender a respiração e fazer força) aumenta a pressão intratorácica e, consequentemente, a pressão venosa central. Isso impede que o ar atmosférico seja aspirado para dentro da veia durante a retirada do cateter, prevenindo a embolia gasosa, uma complicação potencialmente fatal.'),
    (9024, 'Fundamentos de Enfermagem', 'IBFC', 'A medida da circunferência do braço é essencial para a escolha do manguito correto na aferição da PA. A largura da bolsa de borracha do manguito deve corresponder a aproximadamente:', '[
        {"id": "A", "text": "20% da circunferência do braço."},
        {"id": "B", "text": "40% da circunferência do braço."},
        {"id": "C", "text": "60% da circunferência do braço."},
        {"id": "D", "text": "80% da circunferência do braço."},
        {"id": "E", "text": "100% da circunferência do braço."}
    ]', 'B', 'As diretrizes recomendam que a largura da bolsa inflável do manguito seja de aproximadamente 40% da circunferência do braço, e o comprimento deve ser de pelo menos 80%. A escolha correta do manguito é crucial para uma medida acurada da pressão arterial.'),
    (9025, 'Fundamentos de Enfermagem', 'FGV', 'Qual das seguintes opções descreve corretamente a Posição de Sims?', '[
        {"id": "A", "text": "Decúbito dorsal com os joelhos flexionados e os pés apoiados na cama."},
        {"id": "B", "text": "Decúbito ventral com a cabeça virada para o lado."},
        {"id": "C", "text": "Decúbito lateral esquerdo, com a perna esquerda estendida e a direita flexionada."},
        {"id": "D", "text": "Sentado com a cabeceira elevada a 90 graus e uma mesa de apoio."},
        {"id": "E", "text": "Decúbito dorsal com a cabeça mais baixa que o corpo."}
    ]', 'C', 'A Posição de Sims, ou decúbito lateral esquerdo, é caracterizada pelo paciente deitado sobre o lado esquerdo, com a perna esquerda estendida ou ligeiramente fletida, e a perna direita fletida sobre o abdome. É comumente utilizada para exames retais e administração de enemas.'),
    (9026, 'Fundamentos de Enfermagem', 'Cebraspe', 'A avaliação da dor como 5º sinal vital é uma prática essencial. Em um paciente pediátrico não verbal, qual das seguintes escalas é a mais apropriada para avaliar a dor?', '[
        {"id": "A", "text": "Escala Visual Analógica (EVA)."},
        {"id": "B", "text": "Escala Numérica de Dor."},
        {"id": "C", "text": "Escala de Faces de Wong-Baker."},
        {"id": "D", "text": "Escala FLACC."},
        {"id": "E", "text": "Escala de Glasgow."}
    ]', 'D', 'A Escala FLACC (Face, Legs, Activity, Cry, Consolability) é uma ferramenta de avaliação comportamental da dor, ideal para crianças de 2 meses a 7 anos ou pacientes não verbais. A Escala de Faces de Wong-Baker, embora use figuras, ainda requer que a criança consiga apontar e se expressar, o que pode não ser possível em pré-verbais.');